import data from "../Constants/data.json"

const initialState = {
  products: data,
  cartList: []
};


export default function (state = initialState, action) {
  switch (action.type) {
    case "ADD TO CART": {
      let list = JSON.parse(JSON.stringify(state.products))


      let index = state.cartList.findIndex(x => x.id === list[action.index].id)
      if (index > -1) {
        list[action.index]["quanitity"]++
        state.cartList[index]["quanitity"]++
        state.cartList[index]["updatedRate"] = (state.cartList[index]["quanitity"] * state.cartList[index]["rate"])
      } else {
        list[action.index]["quanitity"]++
        console.log(list[action.index], "**********")
        state.cartList.push(list[action.index])
        // state.cartList[index]["quanitity"]++
      }
      state.products = list
      return {
        ...state,
      };
    }

    default:
      return state;
  }
}
