import React from 'react';
import logo from '../logo.svg';
import "../App.css"
import data from "../Constants/data.json"
const words = ['sky', 'blue', 'falcon', 'wood', 'cloud'];
import {useSelector, useDispatch} from 'react-redux';
function Products() {
    const productData = useSelector((state) => state);
    const dispatch = useDispatch();

    return (

        <div className="App">
            <h1>Amazing Recipes</h1>
            <div class="shadow">
                <div class="container">

                    {productData.map((item, index) => (
                        <div class="card">
                            <div className="imgContainer">
                                <img className="img" src={item.imageURL} id="voicebutton" />
                            </div>
                            <span className="title">{item.name}</span>
                            <div className="steps">
                                <span className="stepsdetails">{item.steps[0]}{"\n"}{item.steps[1]} </span>
                            </div>

                            <button type="button" class="btn btn-danger">Add To Cart</button>

                        </div>
                    ))}
                </div>
            </div>
        </div>

    );
}

export default Products;
