import data from "../Constants/data.json"

const initialState = {
  products: data,
};

export default function(state = initialState, action) {
  switch (action.type) {
    case "PRODUCT": {
      const { id, content } = action.payload;
      return {
        ...state,
      };
    }

    default:
      return state;
  }
}
