import data from "../Constants/data.json"

const initialState = {
  products: data,
};

export default function(state = initialState, action) {
  switch (action.type) {
    case "ADD TO CART": {
      let list = JSON.parse(JSON.stringify(state.products))
      console.log(action, list)
      list[action.index]["quanitity"]++
      state.products = list
      return {
        ...state,
      };
    }

    default:
      return state;
  }
}
