import data from "../Constants/data.json"

const initialState = {
  products: data,
  cartList: []
};


function getTotalAmt(){
  return this.reduce((a, b) => a + (b["rate"] || 0), 0);
}


export default function (state = initialState, action) {
  switch (action.type) {
    case "ADD TO CART": {
      let list = JSON.parse(JSON.stringify(state.products))
      list[action.index]["quanitity"]++
      let index = state.cartList.findIndex(x => x.id === list[action.index].id)
      if (index > -1) {
        state.cartList[index]["quanitity"]++
      } else {
        state.cartList.push(list[action.index])
      }
      state.products = list
      return {
        ...state,
      };
    }

    default:
      return state;
  }
}
